
function Helper(){};Helper.prototype.currentDate=function(){var today=new Date(),todayYearMonthDay=(new Date(today.getFullYear(),today.getMonth(),today.getDate())).valueOf();return todayYearMonthDay;};Helper.prototype.returnDate=function(date,language){var now=this.currentDate(),splittedTime=date.split(' '),imputDate=new Date(date),imputeYearMonthDay=(new Date(imputDate.getFullYear(),imputDate.getMonth(),imputDate.getDate())).valueOf();if(now==imputeYearMonthDay){return"Сегодня "+splittedTime[1].slice(0,-3);}else{return imputDate.getDate()+' '+this.getMonth(language,imputDate.getMonth())+' '+imputDate.getFullYear();}};Helper.prototype.getMonth=function(language,month){var mounthObject={ru:{0:'Января',1:'Февраля',2:'Марта',3:'Апреля',4:'Мая',5:'Июня',6:'Июля',7:'Августа',8:'Сентября',9:'Октября',10:'Ноября',11:'Декабря'},uk:{0:"Січня",1:"Лютого",2:"Березня",3:"Квітня",4:"Травня",5:"Червня",6:"Липня",7:"Серпня",8:"Вересня",9:"Жовтня",10:"Листопада",11:"Грудня"}};return mounthObject[language][month];};function App(){};App.prototype=Object.create(Helper.prototype);App.prototype.constructor=App;function CreateIframes(div){if(!div){return};this.div=div;}
CreateIframes.prototype.getIframes=function(){var links=this.div.getAttribute('data-src').split(',');var str='';for(var i=0;i<links.length;i++){str+='<div class="val-outer-frame">'+'<span class="vol-ico-online">'+'<i class="online"></i>'+'</span>'+'<iframe src="'+links[i]+'"></iframe>'+'</div>';};return str;}
CreateIframes.prototype.appendIframes=function(){this.div.insertAdjacentHTML('afterBegin',this.getIframes());}
var divBloc=document.querySelector('.val-iframe-streams');var showIframes=new CreateIframes(divBloc);showIframes.appendIframes();